import React, { Component } from 'react';
import { TextInput} from 'react-native'
import Modal, {
    ModalTitle,
    ModalContent,
    ModalFooter,
    ModalButton,
  } from 'react-native-modals';

export default class Prompt extends Component  {

    constructor(props) {
        super(props)
        this.state = {
            visible: false,
            text:props.text === undefined ? "" : props.text,
        }
    }

    /**
     * 展示
     */
    show() { 
        this.setState({visible: true}) 
    }

    /**
     * 隐藏
     */
    hide() {
        this.setState({
            visible: false,
            text:this.props.text === undefined ? "" : this.props.text,
        }) 
    }

    render() {
        return (
            <Modal
                width={0.9}
                visible={this.state.visible}
                rounded
                actionsBordered
                onTouchOutside={() => {
                    this.hide()
                }}
                modalTitle={
                    <ModalTitle
                        title={this.props.title === undefined ? "请输入" : this.props.title}
                        align="left"
                    />
                }
                footer={
                    <ModalFooter>
                    <ModalButton
                        text="取消"
                        bordered
                        onPress={() => {
                            this.hide()
                        }}
                        key="button-1"
                    />
                    <ModalButton
                        text="确定"
                        bordered
                        onPress={() => {
                            const success = this.props.success
                            if (success != undefined) {
                                success(this.state.text)
                            }
                            this.hide()
                        }}
                        key="button-2"
                    />
                    </ModalFooter>
                }
                >
                <ModalContent
                    style={{
                        backgroundColor: '#fff',
                        backgroundColor: "green",
                    }}
                >
                    {/* <TextInput
                        value={this.state.text}
                        onChangeText={(text) => {
                            this.setState({
                                text
                            })
                        }}
                        style={{
                            backgroundColor:"red",
                        }}
                    /> */}
                    <Text>测试</Text>
                </ModalContent>
            </Modal>
        )
    }
}
