import React, {Component} from 'react';
import {
    View,
    Text,
    StyleSheet,
    Image,
    Modal
} from 'react-native';
import Container from "../../components/container"
import { TouchableOpacity } from 'react-native-gesture-handler';


export default class TaskDetail extends Component {
  render () {
    return (
        <Container
            style={{
                backgroundColor: "white",
                height:"100%"
            }}
        >
        <View style={styles.header}>
            <Text
                style={{
                    fontSize: 24,
                    fontWeight:"bold",
                }}
            >
                这就是理科生的浪漫吧
            </Text>
            <View style={{flexDirection:"row"}}>
                <Image
                    source={{ uri: "https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=815492990,3276551662&fm=26&gp=0.jpg" }}
                    style={{
                        height: 60,
                        width: 60,
                        borderRadius: 60,
                        margin:10,
                    }}
                />    
                <View style={{justifyContent:"center"}}>
                    <Text style={{fontSize:22}}>六分仪</Text>
                    <Text style={{color:"#CBCDCC"}}>6分钟前</Text>
                </View>
            </View>
        </View>
        <View style={styles.body}>
            <Text style={{color:"#E6E6E6",marginHorizontal:15}}>她两次位于仅处于上帝的位置,却两次以爱的名义把世界推向深渊,而这一次没有人能替他挽回</Text>   
            <Image
                source={{ uri: "https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=815492990,3276551662&fm=26&gp=0.jpg" }}
                style={{
                    height: 200,
                    borderRadius: 15,
                    marginHorizontal: 15,
                    marginVertical:10,
                }}
            />    
        </View>    
        <View style={styles.footer}>
            <Text style={styles.footerText}>要求人数：20</Text>
            <Text style={styles.footerText}>当前人数：12</Text>
            <Text style={styles.footerText}>2019-10-12 13:59:59</Text>
        </View>
        <View style={styles.statusBar}>
            <TouchableOpacity style={{
                backgroundColor: "#5CBA6C",
                paddingVertical:10,
                borderRadius:20,
            }}>
                <Text style={{textAlign:"center",color:"#fff",fontSize:16}}>报名</Text>
            </TouchableOpacity>
        </View>
      </Container>
    )
  }
}

const styles = StyleSheet.create({
    header: {

    },
    body: {

    },
    footer: {
        alignItems:"flex-end",
    },
    statusBar: {
        marginTop:10,
    },
    footerText: {
        marginRight: 15,
        marginVertical: 8,
        fontSize:16
    }
})
