import React, { Component } from 'react';
import {
    StyleSheet, Text, TextInput,
    View, Dimensions, StatusBar, TouchableOpacity
} from 'react-native';
import User from "../../api/user"
import Utils from '../../utils';

export default class Login extends Component {
    constructor(props) {
        super(props)
        this.state = {
            tab: 0,
            username: "",
            password: "",
        }
        console.log(props)
    }
    changeTab(tab) {
        if (tab == this.state.tab) {
            return 
        }
        this.setState({
            tab,
            username: "",
            password:"",
        })
    }
    getActivitedTab() {
        if (this.state.tab == 0) {
            return (
                <View style={styles.tab}>
                    <View><Text style={styles.tabFontCover} onPress={()=>this.changeTab(0)}>登录</Text></View>
                    <View><Text style={styles.tabFont} onPress={()=>this.changeTab(1)}>注册</Text></View>
                </View>
            )
        } else {
            return (
                <View style={styles.tab}>
                    <View><Text style={styles.tabFont} onPress={()=>this.changeTab(0)}>登录</Text></View>
                    <View><Text style={styles.tabFontCover} onPress={()=>this.changeTab(1)}>注册</Text></View>
                </View>
            )
        }
    }
    /**
     *  登录操作
     */
    loginHandle() {
        /**
         * 直接跳转
         */

        if (!Utils.isValidPhone(this.state.username)) {
            alert("请输入11位合法手机号!")
            return 
        }
        try {
            response = User.login(this.state.username, this.state.password)
            response.then((json) => {
                const { status, data: token, message } = json
                if (status != 0) {
                    alert("message")
                } else {
                    //TODO 设置token后跳转到主页
                }
            }).catch((err)=>console.log(err))
        } catch(e) {
            console.log(e)
            alert("服务端内部错误")
        }
    }
    /**
     *  渲染
     */
    render() {
        return (
            <View style={styles.container}>
                <View style={styles.header}>
                </View>
                { this.getActivitedTab()}
                <View style={styles.loginBar}>
                    <TextInput
                        keyboardType="numeric"
                        placeholder="请输入手机号"
                        style={styles.input}
                        onChangeText={(username) => {
                            this.setState({
                                username
                            })
                        }}
                        value={this.state.username} />
                    <TextInput
                        password
                        placeholder="请输入密码"
                        style={styles.input}
                        secureTextEntry
                        onChangeText={(password) => {
                            this.setState({
                                password
                            })
                        }}
                        value={this.state.password}
                    />
                </View>
                <View style={styles.loginButtonContainer}>
                    <TouchableOpacity
                        onPress={()=>this.loginHandle()}
                        style={styles.loginButton}>
                        <Text style={{textAlign:"center",color:"white",fontSize:16}}>登录</Text>
                    </TouchableOpacity>
                </View>
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
      flex:1,
      flexDirection:'column',
      backgroundColor: '#fff',
      width: Dimensions.get('window').width * 0.8,
      marginLeft: Dimensions.get('window').width * 0.1,
    },
    header: {
      height: Dimensions.get('window').height * 0.15,
      marginTop: StatusBar.height
    },
    tab: {
        flexDirection: 'row',
    },
    tabFont: {
        fontSize: 18,
        color: "#c1c6c6",
        marginRight:10,
    },
    tabFontCover: {
        fontSize: 28,
        fontWeight:"bold",
        color: "black",
        marginRight: 10,
    },
    loginBar: {
      marginTop:20
    },
    bottom: {
      marginTop:Dimensions.get('window').height * 0.5,
      alignSelf: "center",
    },
    loginButton: {
        backgroundColor: "#68C066",  
        justifyContent: "center",
        borderRadius: 16,
        height: 40,
        width:Dimensions.get('window').width * 0.8,
    },
    loginButtonContainer: {
       marginTop:60,
    },
    input: {
        height: 50,
        borderBottomWidth: 1,
        borderBottomColor: "#C1C6CA",
        marginBottom: 20
    }
  });
