import React, { Component } from 'react';
import { View, Text, StyleSheet,FlatList,Image,Dimensions,TouchableOpacity} from 'react-native';
import Goods from "../../mock/goods"
import Tasks from "../../mock/tasks"
import GoodsDetail from "../../pages/goodsDetail"
import Container from "../../components/container"

const TASK_CENTER = 0 //‰ªªÂä°‰∏≠ÂøÉ
const EXCHANGE = 1  //ÂÖëÊç¢Â•ΩÁ§º

export default class Goto extends Component {
    constructor(props) {
        super(props)
        const goods = []
        let goodsItem = []
        for (let i = 0; i < Goods.length / 2; i++) {
            goodsItem[0] = Goods[i]
            if (i + 1 < Goods.length) {
                goodsItem[1] = Goods[i+1]
            }
            goods[i] = goodsItem
        }
        this.state = {
            goods,
            tasks:Tasks,
            tab: TASK_CENTER, //ÈªòËÆ§ÈÄâÊã©Á¨¨‰∏Ä‰∏™ÂÖÉÁ¥†
            showModal:false,
        }
    }
    //‰ªªÂä°‰∏≠ÂøÉ
    task() {
        const { navigate } = this.props.navigation
        return (
            <FlatList
                style={styles.taskBody}
                data={this.state.tasks}
                showsVerticalScrollIndicator={false}
                renderItem={
                    ({ index, item }) => {
                        return (
                            <TouchableOpacity
                                style={{
                                    flexDirection: "row",
                                    justifyContent: "space-between",
                                    paddingVertical: 20,
                                    paddingHorizontal: 14,
                                    marginBottom: 22,
                                    backgroundColor:"#fff",
                                    borderRadius:10,
                                }}
                                onPress={() => {
                                    navigate('TaskDetail',{taskId:1})
                                }}
                            >
                                <View style={{
                                    flexBasis: "55%",
                                    justifyContent: "space-between",
                                }}>
                                    <Text style={{fontSize:16,fontWeight:"bold"}}>{item.title}</Text>
                                    <Text style={{fontSize:15}}>{item.description}</Text>
                                    <View style={{flexDirection: "row"}}>
                                        <Text
                                            style={{
                                                backgroundColor: "#F2F9F2",
                                                borderRadius: 16,
                                                paddingVertical: 5,
                                                paddingHorizontal: 10,
                                                color: "#9DD39A",
                                                textAlign: "center",
                                            }}>
                                            #{item.address}
                                        </Text>
                                    </View>
                                </View>
                                <Image source={{ uri: item.image }} style={{ height:120,width:"40%",borderRadius:10 }}/>
                            </TouchableOpacity>
                        )
                    }
                }
            />
       )
    }
    //Â•ñÂìÅÂÖëÊç¢
    exchange() {
        return (
            <FlatList
                style={styles.body}
                data={this.state.goods}
                showsVerticalScrollIndicator={false}
                renderItem={
                    ({index,item}) => this.viewItemBuilder(index,item)
                }
            />
        )
    }
    viewItemBuilder(index, item) {
       // let value = item[0]
     //   let value = item[1]
        return (
            <View style={styles.itemBox} key={index}>
                {
                    item.map((value) => {
                        return (
                            <TouchableOpacity
                                style={styles.item}
                                onPress={() => this.refs.goods.display()}
                            >
                                <Image
                                    source={{ uri: value.image }}
                                    style={{
                                        height: 180,
                                        borderRadius:10,
                                    }}
                                />
                                <View style={styles.itemMsg}>
                                    <Text style={styles.title}>{value.name}</Text>
                                    <Text style={styles.description}>{value.description}</Text>
                                    <Text style={styles.points}>üí∞{value.points}</Text>
                                </View>    
                            </TouchableOpacity>
                        )
                    })
                }
            </View>
        )
    }

    render() {
        return (
            <Container>
                <GoodsDetail
                    navigation = {this.props.navigation}
                    ref='goods'
                />
                <View style={styles.header}>
                    <View style={{ flexDirection: "row",alignItems:"baseline" }}>
                        <Text
                            style={this.state.tab == TASK_CENTER ? styles.tabCovers : styles.tab}
                            onPress={() => this.setState({ tab: TASK_CENTER })}
                        >
                            ‰ªªÂä°‰∏≠ÂøÉ
                        </Text>
                        <Text
                            style={this.state.tab == EXCHANGE ? styles.tabCovers : styles.tab}
                            onPress={() => this.setState({ tab: EXCHANGE })}
                        >
                            ÂÖëÊç¢Â•ΩÁ§º
                        </Text>
                    </View>
                    <Text>üí∞20</Text>
                </View>
                {
                    this.state.tab == EXCHANGE ? this.task() : this.exchange()
                }
            </Container>
       )
    }
}

const styles = StyleSheet.create({
    header: {
        flexDirection: "row",
        justifyContent: "space-between",
        backgroundColor: "#F1F3F0",
        alignItems: "baseline",
        paddingVertical:20,
    },
    body: {
        alignSelf:"center",
        width:Dimensions.get('window').width * 0.8,
        backgroundColor: "#F1F3F0",
        marginBottom:60,
    },
    taskBody: {
        backgroundColor: "#F1F3F0",  
        marginBottom:60,
    },
    itemBox: {
        flexDirection: "row",
        justifyContent: "space-between",
        marginBottom:22,

    },
    item: {
        flex: 0.48,
        backgroundColor: "#fff",
        borderRadius:10,
    },
    itemMsg: {
        padding: 10,

    },
    title: {
        fontSize: 18,
        fontWeight: "bold",
        paddingBottom:4,
    },
    description: {
        color: "#C3C4C4",
        paddingBottom:6,
    },
    points: {
        color: "#C3E5C1",
        fontWeight: "bold",
        fontSize:18,
    },
    tab: {
        color: "#C3C4C4",
        marginRight:10,
    },
    tabCovers: {
        fontSize: 24,
        marginRight:10,
    }
})
