import React, { Component } from 'react';
import { View, Text, StyleSheet, FlatList, DeviceEventEmitter, TouchableOpacity } from 'react-native';
import Mock from "../../mock/address"
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';



export default class Address extends Component {
    constructor(props) {
        super(props)
        this.state = {
            records : Mock,
        }
    }
    viewItemBuilder(index, item) {
        const navigation = this.props.navigation
        return (
            <TouchableOpacity
                style={styles.addressItem}
                key={index}
                onPress={
                    () => {
                        navigation.goBack()
                        DeviceEventEmitter.emit("ChooseAddress")
                    }
                }
            >
                <View style={{
                    flexDirection: "row",
                    justifyContent: "space-between",
                    marginBottom:10,
                }}>
                    <Text style={{fontSize:22}}>{item.name}</Text>
                    <Text style={{fontSize:18}}>{item.phone}</Text>
                </View>
                <Text style={{fontSize:16}}>{item.detail}</Text>
            </TouchableOpacity>
        )
    }
    render() {
        return (
            <View style={styles.container}>
                <View style={styles.header}>
                    <Text style={{backgroundColor: "#F2F2F2",paddingVertical:10}}>当前配送至</Text>
                    <View style={{
                        flexDirection: "row", alignItems: "center",marginLeft:10
                    }}>
                        <MaterialIcons
                            size={26}
                            name={'location-on'}
                        />
                        <Text style={{fontSize:18,paddingVertical:6}}>广州市 番禺区 大石街道</Text>
                    </View>
                </View>
                <View style={styles.body}>
                    <Text style={{backgroundColor: "#F2F2F2",paddingVertical:10}}>从我的收货地址中选择</Text>
                </View>
                <FlatList
                    data={this.state.records}
                    showsVerticalScrollIndicator={false}
                    style={{backgroundColor:"#fff"}}
                    renderItem={
                        ({index,item}) => this.viewItemBuilder(index,item)
                    }
                />
            </View>
        )
    }
} 

const styles = StyleSheet.create({
    // container: {
    //     marginTop: StatusBar.currentHeight,
    // },  
    header: {
        backgroundColor:"#fff"
    },
    body: {

    },
    addressItem: {
        width: "90%",
        alignSelf: "center",
        borderBottomWidth: 1,
        borderColor: "#B3B3B3",
        paddingVertical: 10,
    }
})
