import Activity from "../activity"
import Merchants from "../merchants"
import Personal from "../personal"
import SignUp from "../signUp"
import Goto from "../goto"
import TaskDetail from "../taskDetail"
import GoodsDetail from "../goodsDetail"
import Address from "../address"
import Myself from "../myself"
import Test from "../test"
import { createAppContainer, createSwitchNavigator } from "react-navigation"
import {createBottomTabNavigator} from 'react-navigation-tabs';
import { createStackNavigator } from 'react-navigation-stack';
import React from 'react';
import Colors from "../../constants/colors"
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import Login from  "../login";

export const BottomTab = createAppContainer(
  createBottomTabNavigator(
    {
      /*Page1路由*/
      Activity: {
        /*Page1页面*/
        screen: Activity,
        /*屏幕导航选项,可以定制导航器显示屏幕的方式（头部标题，选项卡标签等）*/
        navigationOptions: {
          /*导航标签名*/
          tabBarLabel: '活动中心',
          /*导航呈现的图标*/
          tabBarIcon: ({tintColor, focused}) => (
            /*第三方图标库（图标名称，图标大小，图标样式*/
            <MaterialIcons
              name={'home'}
              size={26}
              style={{ color: Colors.tab.activeColor }}
            />
          ),
        },
      },
      Merchants: {
        screen: Merchants,
        navigationOptions: {
          tabBarLabel: '商家列表',
          tabBarIcon: ({tintColor, focused}) => (
            <MaterialIcons
              name={'location-on'}
              size={26}
              style={{ color: Colors.tab.activeColor }}
            />
          ),
        },
      },
      SignUp: {
        screen: SignUp,
        navigationOptions: {
          tabBarLabel: '我要打卡',
          tabBarIcon: ({tintColor, focused}) => (
            <MaterialIcons
              name={'assignment'}
              size={26}
              style={{color: Colors.tab.activeColor}}
            />
          ),
        },
      },
      Personal: {
        screen: Personal,
        navigationOptions: {
          tabBarLabel: '个人主页',
          tabBarIcon: ({tintColor, focused}) => (
            <MaterialIcons
              name={'perm-identity'}
              size={26}
              style={{color: Colors.tab.activeColor}}
            />
          ),
        },
      },
    },
    {
      tabBarOptions: {
        /*设置活动选项卡标签的颜色*/
        activeTintColor: Platform.OS === 'ios' ? '#06C1AE' : Colors.tab.activeColor,
        indicatorStyle:{
            backgroundColor: "red",
            height:5
        },
        // labelStyle:{
        //     backgroundColor:"black"
        // },
        style:{
            backgroundColor:"white",
        }
      },
    },
  ),
);

const StackRouter = createStackNavigator({
  Merchants: {
    screen: Merchants,
    navigationOptions: {
      headerShown: false, 
    },
  },
  Goto: {
    screen: Goto,
    navigationOptions: {
      headerShown: false, 
    },
  },
  TaskDetail: {
    screen: TaskDetail,
    navigationOptions: {
      headerShown: false, 
    },
  },
  GoodsDetail: {
    screen: GoodsDetail,
    navigationOptions: {
      headerShown: false, 
    },
  },
  Address: {
    screen: Address,
    navigationOptions: {
      headerShown: true,
      title:"地址",
    },
  },
  Myself: {
    screen: Myself,
    navigationOptions: {
      headerShown: true,
      title:"个人信息",
    },
  },
});

const isLogin = true 
const Nav = createAppContainer(
  createSwitchNavigator(
    {
      Home: {
        screen: BottomTab,
        navigationOptions: {
          headerShown: false, 
        },
      },
      Login: {
        screen: Login,
        navigationOptions: {
          headerShown: false, 
        },
      },
      StackRouter:{
        screen: StackRouter,
        navigationOptions: {
          headerShown: false, 
        },
      },
      Test: {
        screen: Test,
        navigationOptions: {
          headerShown: false, 
        },
      }
    },
    {
      initialRouteName: !isLogin ? 'Login' : 'Home',
      // navigationOptions:{ //导航栏通用设置
      //   headerStyle:{
      //     backgroundColor:'#7276ff'
      //   }
      // }
    },
  ),
);

export default Nav
