import React, {Component} from 'react';
import {
  View, Image,Text,StyleSheet,TouchableOpacity,Platform
} from 'react-native';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import * as ImagePicker from 'expo-image-picker';
import * as Permissions from 'expo-permissions';
import Prompt from "../../components/modal/prompt"

export default class Myself extends Component {

    constructor(props) {
        super(props)
        this.state = {
            photo:"https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=815492990,3276551662&fm=26&gp=0.jpg"
        }
    }

    componentDidMount() {
        this.getPermissionAsync();
        console.log(this.state.photo);
    }

    getPermissionAsync = async () => {
        if (Platform.OS === "ios") {
          const { status } = await Permissions.askAsync(Permissions.CAMERA_ROLL);
          if (status !== 'granted') {
            alert('Sorry, we need camera roll permissions to make this work!');
          }
        }
    }

    album = async () => {  
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.All,
            allowsEditing: true,
            aspect: [4, 3],
            quality: 1
          });

        console.log(result);
        if (!result.cancelled) {
            this.setState({ photo: result.uri });
        }
    }

    render() {
        return (
            <View style={styles.container}>
                <TouchableOpacity style={styles.item} onPress={()=>this.album()}>
                    <Text>头像</Text>
                    <View style={styles.right}>
                        <Image
                            source={{ uri: this.state.photo }}
                            style={{width:80,height:80,borderRadius:5}}
                        />
                        <MaterialIcons
                            size={26}
                            name="keyboard-arrow-right"
                            style={styles.icon}
                        />
                    </View>
                </TouchableOpacity>
                <Prompt ref="prompt"/>
                <TouchableOpacity
                    style={styles.item}
                    onPress={()=>this.refs.prompt.show()}
                >
                    <Text>昵称</Text>
                    <View style={styles.right}>
                        <Text style={{color:"#B3B3B3"}}>帅羊</Text>
                        <MaterialIcons 
                            size={26}
                            name="keyboard-arrow-right"
                            style={styles.icon}
                        />
                    </View>
                </TouchableOpacity>
                <View style={styles.item}>
                    <Text>微信</Text>
                    <View style={styles.right}>
                        <Text style={{color:"#B3B3B3"}}>已绑定</Text>
                        <MaterialIcons 
                            size={26}
                            name="keyboard-arrow-right"
                            style={styles.icon}
                        />
                    </View>
                </View>
                <View style={styles.item}>
                    <Text>qq</Text>
                    <View style={styles.right}>
                        <Text>已绑定</Text>
                        <MaterialIcons 
                            size={26}
                            name="keyboard-arrow-right"
                            style={styles.icon}
                        />
                    </View>
                </View>
                <View style={styles.item}>
                    <Text>手机号</Text>
                    <View style={styles.right}>
                        <Text>已绑定</Text>
                        <MaterialIcons 
                            size={26}
                            name="keyboard-arrow-right"
                            style={styles.icon}
                        />
                    </View>
                </View>
            </View>
        )
    }
}

const styles = StyleSheet.create({
    item: {
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center",
        paddingVertical: 15,
        borderBottomWidth: 1,
        borderBottomColor: "#FAFAFA",
        paddingLeft: 10,
        backgroundColor: "white",
    },
    right: {
        flexDirection: "row",
        alignItems:"center",
    },
    icon: {
        color:"#EEEEEE",
    }
})
